package leetCode

import org.scalatest.FunSuite
import scala.collection.JavaConverters._

class WordBreakTest extends FunSuite {

  test("test sample case.") {
    val dict = Set("leet", "code")
    val result = new WordBreak().wordBreak("leetcode", dict.asJava)
    assert(result === true)

    val resultII = new WordBreak().wordBreakII("leetcode", dict.asJava)
    println(resultII.asScala.mkString("/n"))
  }

  test("test aaa") {
    val dict = Set("aaaa","aa")
    val result = new WordBreak().wordBreak("aaaaaaa", dict.asJava)
    assert(result == false)

    val resultII = new WordBreak().wordBreakII("aaaaaaa", dict.asJava)
    println(resultII.asScala.mkString("/n"))

    val dict1 = Set("a")
    val resultII1 = new WordBreak().wordBreakII("a", dict1.asJava)
    println(resultII1.asScala.mkString("/n"))
    
    val dict2 = Set("a", "b")
    val resultII2 = new WordBreak().wordBreakII("ab", dict2.asJava)
    println(resultII2.asScala.mkString("/n"))
  }

