  test("test performance") {
    val startTime = System.currentTimeMillis()
    val dict = Set("a","aa","aaa","aaaa","aaaaa","aaaaaa","aaaaaaa","aaaaaaaa","aaaaaaaaa","aaaaaaaaaa")
    val result = new WordBreak().wordBreak("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaab", dict.asJava)
    println("Time consumed : " + (System.currentTimeMillis()-startTime))
    assert(result === false)
    val resultII = new WordBreak().wordBreakII("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaab", dict.asJava)
    println(resultII.asScala.mkString("/n"))
  }

  test("test word break II sample") {
    val dict = Set("cat","cats","and","sand","dog")
    val result = new WordBreak().wordBreakII("catsanddog", dict.asJava)
    println(result.asScala.mkString("\n"))
  }

  test("test word break II sample 2") {
    val dict = Set("aaaa","aa","a")
    val result = new WordBreak().wordBreakII("aaaaaaa", dict.asJava)
    println(result.asScala.mkString("\n"))
  }
